bot.command('craw', async (ctx) => {
    try {
        // Cek apakah ini reply message
        const isReply = ctx.message.reply_to_message;
        const input = ctx.message.text.replace('/craw', '').trim();

        let content, filename;

        if (isReply) {
            // Mode 1: Reply message + filename
            if (!input) {
                return ctx.reply(
                    'Masukin : /craw Nama File\nExample : /craw File.py'
                );
            }
            content = ctx.message.reply_to_message.text  ctx.message.reply_to_message.caption  '';
            filename = input;
        } else {
            // Mode 2: Traditional Code|Nama File
            if (!input) {
                return ctx.reply(
                    'Masukin : Code|Nama File\nExample : Hello World|File.py'
                );
            }

            const parts = input.split('|');

            if (parts.length !== 2) {
                return ctx.reply(
                    'Masukin : Code|Nama File\nExample : Hello World|File.py'
                );
            }

            [content, filename] = parts.map(part => part.trim());
        }

        if (!content || !filename) {
            return ctx.reply(
                'Text atau nama file tidak boleh kosong!\n' +
                'Contoh: Hello World|Test.py'
            );
