        const validFilename = filename.replace(/[^a-zA-Z0-9._-]/g, '_');

        const processingMsg = await ctx.reply('Mengupload ke Vortex Code...', { 
            parse_mode: 'Markdown' 
        });

        const response = await fetch("https://code.vortexx.web.id/api/submit", {
