        const result = await response.json();

        if (result.success) {
            const responseMessage = 
                Berhasil diupload!\n\n +
                â€¢ **File:** \${validFilename}\\n +
                â€¢ **ID:** \${result.id}\``;

            await ctx.telegram.editMessageText(
