                processingMsg.message_id,
                null,
                responseMessage,
                { 
                    parse_mode: 'Markdown',
                    disable_web_page_preview: true
                }
            );

            await ctx.reply(
                'Click Button Dibawah Untuk Melihat Preview Raw And Preview Website',
                {
                    parse_mode: 'Markdown',
                    reply_markup: {
                        inline_keyboard: [
                            [
                                { text: 'á´á´˜á´‡É´ Ê™Ê€á´á´¡sá´‡Ê€', url: https://code.vortexx.web.id/view/${result.id} },
                                { text: 'É¢á´‡á´› Ê€á´€á´¡', url: https://code.vortexx.web.id/api/get/${result.id} }
                            ]
                        ]
                    }
                }
            );

        } else {
            throw new Error(result.error || 'Upload gagal');
        }

    } catch (error) {
        console.error('Bot Error:', error);

        let errorMessage = Error!\n${error.message};

        if (error.message.includes('Failed to fetch')) {
            errorMessage = 'Vortex Code API tidak bisa diakses!\nPeriksa koneksi internet atau coba lagi nanti.';
