import express from "express";
import bodyParser from "body-parser";
import fs from "fs";
import bcrypt from "bcrypt";

const app = express();
const PORT = 3000;

app.use(bodyParser.json());

const USERS_FILE = "./users.json";

// Fungsi baca data user
function readUsers() {
  const data = fs.readFileSync(USERS_FILE, "utf-8");
  return JSON.parse(data);
}

// Fungsi simpan data user
function saveUsers(users) {
  fs.writeFileSync(USERS_FILE, JSON.stringify(users, null, 2));
}

// Register user baru
app.post("/register", async (req, res) => {
  const { username, password } = req.body;
  if (!username || !password) return res.status(400).send("Username & password wajib!");

  const users = readUsers();
  if (users.find(u => u.username === username)) {
    return res.status(400).send("Username sudah dipakai!");
  }

  const hashedPassword = await bcrypt.hash(password, 10);
  users.push({ username, password: hashedPassword });
  saveUsers(users);

  res.send("User berhasil didaftarkan!");
});

// Login user
app.post("/login", async (req, res) => {
  const { username, password } = req.body;
  if (!username || !password) return res.status(400).send("Username & password wajib!");

  const users = readUsers();
  const user = users.find(u => u.username === username);
  if (!user) return res.status(400).send("User tidak ditemukan!");

  const match = await bcrypt.compare(password, user.password);
  if (!match) return res.status(400).send("Password salah!");

  res.send("Login berhasil!");
});

app.listen(PORT, () => {
  console.log(`Server jalan di http://localhost:${PORT}`);
});
